using System.Collections;using UnityEditor;public class TableConfig{	string ASSETPATH = "Assets/config/tabledata/";	public bool loadsuccess;	public void init()	{		loadsuccess = false;		RunSingel.Instance.runTimer(loadData());	}	IEnumerator loadData()	{		AssetDatabase.LoadAssetAtPath<Config_t_ActorMessage>(ASSETPATH + "t_ActorMessage_data.asset").init();		while (!Config_t_ActorMessage.isloaded)			yield return null;		AssetDatabase.LoadAssetAtPath<Config_t_DataCard>(ASSETPATH + "t_DataCard_data.asset").init();		while (!Config_t_DataCard.isloaded)			yield return null;		AssetDatabase.LoadAssetAtPath<Config_t_DefaultCardGroup>(ASSETPATH + "t_DefaultCardGroup_data.asset").init();		while (!Config_t_DefaultCardGroup.isloaded)			yield return null;		loadsuccess = true;	}	public void Dispose()	{		Config_t_ActorMessage.Dispose();		Config_t_DataCard.Dispose();		Config_t_DefaultCardGroup.Dispose();	}}