using System.Collections.Generic;using UnityEditor;using System;public class Config_t_Buff : AssetData{	public List<t_Buff> asset = new List<t_Buff>();	public void addContent(t_Buff item)	{		asset.Add(item);	}	public static bool isloaded;	public static Dictionary<int, t_Buff> _data { get; private set; }	public void init()	{		_data = new Dictionary<int, t_Buff>();		for (int i = 0; i < asset.Count; i++){			_data.Add(asset[i].id, asset[i]);		}		asset = null;		isloaded = true;	}	public static t_Buff getOne(int id)	{		if (_data.ContainsKey(id))			return _data[id];		else			throw new Exception("not find" + id);	}	public static void Dispose()	{		_data = null;	}}[Serializable]public class t_Buff{	public int id;	public string sname;	public string sdes;	public string imgname;	public int taketype;	public int hittype;	public int takenum;}