using System.Collections.Generic;using UnityEditor;using System;public class Config_t_Business : AssetData{	public List<t_Business> asset = new List<t_Business>();	public void addContent(t_Business item)	{		asset.Add(item);	}	public static bool isloaded;	public static Dictionary<int, t_Business> _data { get; private set; }	public void init()	{		_data = new Dictionary<int, t_Business>();		for (int i = 0; i < asset.Count; i++){			_data.Add(asset[i].id, asset[i]);		}		asset = null;		isloaded = true;	}	public static t_Business getOne(int id)	{		if (_data.ContainsKey(id))			return _data[id];		else			throw new Exception("not find" + id);	}	public static void Dispose()	{		_data = null;	}}[Serializable]public class t_Business{	public int id;	public int itemid;	public int postype;	public int salenum;}