using System.Collections.Generic;using UnityEditor;using System;public class Config_t_Consumable : AssetData{	public List<t_Consumable> asset = new List<t_Consumable>();	public void addContent(t_Consumable item)	{		asset.Add(item);	}	public static bool isloaded;	public static Dictionary<int, t_Consumable> _data { get; private set; }	public void init()	{		_data = new Dictionary<int, t_Consumable>();		for (int i = 0; i < asset.Count; i++){			_data.Add(asset[i].id, asset[i]);		}		asset = null;		isloaded = true;	}	public static t_Consumable getOne(int id)	{		if (_data.ContainsKey(id))			return _data[id];		else			throw new Exception("not find" + id);	}	public static void Dispose()	{		_data = null;	}}[Serializable]public class t_Consumable{	public int id;	public string sname;	public string remark;	public ItemsType type;	public int rewardType;	public int takeid;	public int takenum;	public int takenum2;	public int rewardGiven;	public int rewardNum;	public string rewardPool;}