using System.Collections.Generic;using UnityEditor;using System;public class Config_t_DefaultCardGroup : AssetData{	public List<t_DefaultCardGroup> asset = new List<t_DefaultCardGroup>();	public void addContent(t_DefaultCardGroup item)	{		asset.Add(item);	}	public static bool isloaded;	public static Dictionary<int, t_DefaultCardGroup> _data { get; private set; }	public void init()	{		_data = new Dictionary<int, t_DefaultCardGroup>();		for (int i = 0; i < asset.Count; i++){			_data.Add(asset[i].id, asset[i]);		}		asset = null;		isloaded = true;	}	public static t_DefaultCardGroup getOne(int id)	{		if (_data.ContainsKey(id))			return _data[id];		else			throw new Exception("not find" + id);	}	public static void Dispose()	{		_data = null;	}}[Serializable]public class t_DefaultCardGroup{	public int id;	public string cardsname;	public string cardlist;}