using System.Collections.Generic;using UnityEditor;using System;public class Config_t_ExplorMap : AssetData{	public List<t_ExplorMap> asset = new List<t_ExplorMap>();	public void addContent(t_ExplorMap item)	{		asset.Add(item);	}	public static bool isloaded;	public static Dictionary<int, t_ExplorMap> _data { get; private set; }	public void init()	{		_data = new Dictionary<int, t_ExplorMap>();		for (int i = 0; i < asset.Count; i++){			_data.Add(asset[i].id, asset[i]);		}		asset = null;		isloaded = true;	}	public static t_ExplorMap getOne(int id)	{		if (_data.ContainsKey(id))			return _data[id];		else			throw new Exception("not find" + id);	}	public static void Dispose()	{		_data = null;	}}[Serializable]public class t_ExplorMap{	public int id;	public string sname;	public string imageName;	public string describe;	public int maplength;	public string mapHelp;	public string mapDayBox;}