using System.Collections.Generic;using UnityEditor;using System;public class Config_t_Offer : AssetData{	public List<t_Offer> asset = new List<t_Offer>();	public void addContent(t_Offer item)	{		asset.Add(item);	}	public static bool isloaded;	public static Dictionary<int, t_Offer> _data { get; private set; }	public void init()	{		_data = new Dictionary<int, t_Offer>();		for (int i = 0; i < asset.Count; i++){			_data.Add(asset[i].id, asset[i]);		}		asset = null;		isloaded = true;	}	public static t_Offer getOne(int id)	{		if (_data.ContainsKey(id))			return _data[id];		else			throw new Exception("not find" + id);	}	public static void Dispose()	{		_data = null;	}}[Serializable]public class t_Offer{	public int id;	public string title;	public int reward;	public string describe;	public int stype;	public int targetID;	public int finishNum;	public int changeID;	public int changeNum;}