using System.Collections.Generic;using UnityEditor;using System;public class Config_t_Plan : AssetData{	public List<t_Plan> asset = new List<t_Plan>();	public void addContent(t_Plan item)	{		asset.Add(item);	}	public static bool isloaded;	public static Dictionary<int, t_Plan> _data { get; private set; }	public void init()	{		_data = new Dictionary<int, t_Plan>();		for (int i = 0; i < asset.Count; i++){			_data.Add(asset[i].id, asset[i]);		}		asset = null;		isloaded = true;	}	public static t_Plan getOne(int id)	{		if (_data.ContainsKey(id))			return _data[id];		else			throw new Exception("not find" + id);	}	public static void Dispose()	{		_data = null;	}}[Serializable]public class t_Plan{	public int id;	public string remark;	public string needid;	public string needcount;	public int finishID;	public int finishType;}