using System.Collections.Generic;using UnityEditor;using System;public class Config_t_UpgradeConfig : AssetData{	public List<t_UpgradeConfig> asset = new List<t_UpgradeConfig>();	public void addContent(t_UpgradeConfig item)	{		asset.Add(item);	}	public static bool isloaded;	public static Dictionary<int, t_UpgradeConfig> _data { get; private set; }	public void init()	{		_data = new Dictionary<int, t_UpgradeConfig>();		for (int i = 0; i < asset.Count; i++){			_data.Add(asset[i].id, asset[i]);		}		asset = null;		isloaded = true;	}	public static t_UpgradeConfig getOne(int id)	{		if (_data.ContainsKey(id))			return _data[id];		else			throw new Exception("not find" + id);	}	public static void Dispose()	{		_data = null;	}}[Serializable]public class t_UpgradeConfig{	public int id;	public string remark;	public string needid;	public string needcount;}