using System.Collections.Generic;using UnityEditor;using System;public class Config_t_crop : AssetData{	public List<t_crop> asset = new List<t_crop>();	public void addContent(t_crop item)	{		asset.Add(item);	}	public static bool isloaded;	public static Dictionary<int, t_crop> _data { get; private set; }	public void init()	{		_data = new Dictionary<int, t_crop>();		for (int i = 0; i < asset.Count; i++){			_data.Add(asset[i].id, asset[i]);		}		asset = null;		isloaded = true;	}	public static t_crop getOne(int id)	{		if (_data.ContainsKey(id))			return _data[id];		else			throw new Exception("not find" + id);	}	public static void Dispose()	{		_data = null;	}}[Serializable]public class t_crop{	public int id;	public string sname;	public int produceCoef;	public int finishID;	public int rareID;}